#'   facet_wrap(~sa_ctrl)
#'
#' # Reproduce main regression model (Sunday Assembly only)
#' sa_data <- sunday_assembly_data %>%
#'   filter(sa_ctrl == "Sunday Assembly")
#'
#' model <- lm(scale(sb6_change) ~ scale(panasp_change) + scale(panasn_change),
#'             data = sa_data)
#' summary(model)
#' }
#'
#' @keywords datasets ritual religion social-bonding affect PANAS wellbeing
"rituals"